mapper.insert:
INSERT INTO sys_log (  						
					user_id ,						
					user_action ,						
					create_time 						
) VALUES ( 						
					#{userId,jdbcType=BIGINT},						
					#{userAction,jdbcType=VARCHAR},						
					#{createTime,jdbcType=TIMESTAMP}						
)

mapper.update:
	UPDATE sys_log	

 				 id=#{id},
			 				 user_id=#{userId},
			 				 user_action=#{userAction},
							 create_time=#{createTime}

	WHERE 			id=#{id}

mapper.delete:
        DELETE FROM sys_log	 

		       id = #{id}  
        		       and user_id = #{userId} 
	    		       and user_action = #{userAction} 
	    		       and create_time = #{createTime}

mapper.select:
	SELECT
			 id as "id" ,			
			 user_id as "userId" ,			
			 user_action as "userAction" ,			
			 create_time as "createTime" 			
	FROM  sys_log  

		       id = #{id}  
        		       and user_id = #{userId} 
	    		       and user_action = #{userAction} 
	    		       and create_time = #{createTime} 

	order by id desc

			limit #{ startIndex }, #{ maxResults }

		SELECT count(1) 
		FROM sys_log 

		       id = #{id}  
        		       and user_id = #{userId} 
	    		       and user_action = #{userAction} 
	    		       and create_time = #{createTime}
